    const [weight, setWeight]=useState(null)
    const [messageResultImc, setMassageImc]=useState("preencha o peso e altura");
    const [imc, setImc]=useState(null)
    const [textButton, setTextButton]=useState("Calcular")

    function imcCalculator(){
        return setImc((weight/(height*height)).toFixed())
    }
    
    return(
       <View>
            <View>
                <Text>Altura</Text>
                <TextInput placeholder="Ex. 1.75" keyboardType="numeric"></TextInput>.
                <Text>Peso</Text>
                <TextInput placeholder="Ex. 76.560" keyboardType="numeric"></TextInput>
                <Button title="calcular imc"/>
            </View>
            <ResultImc messageResultImc={messageImc} resultImc={imc}/>
       </View>
    );

}
